--
-- Структура таблицы `comments`
--

CREATE TABLE `comments` (
  `id` int(11) NOT NULL,
  `id_blog` int(11) NOT NULL,
  `id_user` int(11) NOT NULL,
  `comment` text NOT NULL,
  `data` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `comments`
--

INSERT INTO `comments` (`id`, `id_blog`, `id_user`, `comment`, `data`) VALUES
(1, 7, 5, 'Здравствуйте, хочу поблагодарить Вас за интересный ресурс и очень познавательные видео.', 1622099892),
(2, 2, 2, 'Первое, что хочу написать – СПАСИБО Вам огромное за интересный блог. Пишите интересно, грамотно (что сейчас весьма редко) и я сегодня внес Ваш блог к себе на “экспресс-панель” постоянно посещаемых ресурсов.', 1622100511),
(3, 2, 5, 'Интересный блог уже весь перечитал. Давайте еще!', 1622100589),
(4, 4, 7, 'Привет! Классный блог! Хочу тоже стать фотографом…', 1622101268),
(5, 6, 8, 'Прага — потрясающий город. Я много слышала о нем, как об одном из самых красивых городов мира. Но, как говорится в одной поговорке, лучше один раз увидеть, чем сто раз услышать.', 1622101500),
(6, 6, 4, 'Для меня Прага- это сказочный город-музей под открытым небом, где время не остановилось, а как-бы смешалось. Здесь гармонично сочетается несколько эпох. Это город старинных замков, мостов, соборов и площадей, где по мостовым все-еще разъезжают в каретах, запряженных лошадьми и в ретро-автомобилях. Но современный транспорт и довольно смелые арт-объекты вписались очень гармонично и придали Праге изюминку. Частично архитектурой город напоминает Францию, немного другие европейские страны, но образ в целом совершенно неповторимый и индивидуальный. А еще Прага- это мистический и таинственный город, с ним связано множество интересных и загадочных легенд.', 1622101629),
(7, 6, 3, 'Здравствуйте, мне понравился Ваш блог – много интересных и полезных статей!', 1622101836),
(8, 6, 6, 'Прага - один из любимейших городов Европы!', 1622101965);

