  `lastname` varchar(50) NOT NULL,
  `gender` varchar(10) NOT NULL,
  `phone` varchar(50) NOT NULL,
  `email` varchar(100) NOT NULL,
  `message_subject` varchar(200) NOT NULL,
  `message` text NOT NULL,
  `data_message` int(11) UNSIGNED NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `message`
--

INSERT INTO `message` (`Id`, `firstname`, `lastname`, `gender`, `phone`, `email`, `message_subject`, `message`, `data_message`) VALUES
(1, 'Вероника', 'Орлова', 'female', '89209560323', 'ver.orlova@mail.ru', 'Вопросы', 'Сколько стоит фотосессия? Что входит в неё? Сколько по времени длится фотосессия?', 1620471988),
(2, 'Пётр', 'Петров', 'male', '89308547629', 'petr-petrov@yandex.ru', 'Фотосессия', 'За какое время необходимо записаться на съёмку? Сколько по времени делаются фотографии?', 1620749225),
(3, 'Екатерина', 'Некрасова', 'female', '89067542093', 'nekr-ekaterina@mail.ru', 'Вопросы по фотосессии', 'Предоставляете ли одежду на съёмку и есть ли примерка до съёмки? Можно ли не пользоваться услугами визажиста? Кто отбирает кадры на ретушь? Долго ли обрабатываются фотографии?', 1620749520),
(4, 'Мария', 'Никонова', '', '89054673217', 'maria.nikonova@gmail.com', 'Cемейная съемка', 'Как планируется семейная фотосессия? Где может проходить съемка? Как организуется работа с детьми? Ведь они могут быть капризными, непослушными, застенчивыми? С детьми какого возраста вы можете работать?', 1620749771),
(5, 'Роман', 'Константинов', 'male', '89546521135', 'roma.konst@yandex.ru', 'Запись', 'Когда будет ближайшая запись на фотосессию?', 1620750408),
(6, 'Тимур', 'Коротков', 'male', '89209450326', 'kt@mail.ru', 'Вопросы', 'Как оборудована студия? Можно ли переделать кое-что под определенные пожелания клиента? Имеется ли у фотографа все необходимое оборудование, в том числе и резервное?', 1620750502),
(7, 'Дарья', 'Ларина', 'female', '89765409312', 'darlar@mail.ru', 'Вопросы', 'Какие дополнительные расходы, не включенные в пакет, могут возникать? Можно ли обойтись без профессионального макияжа и прически?', 1620750652);

