) ENGINE=MyISAM DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `reviews`
--

INSERT INTO `reviews` (`Id`, `id_user`, `text`, `data_review`) VALUES
(1, 2, 'Спасибо за позитивный заряд энергии, суперское настроение, которое получаешь от фотосьемки!Было очень приятно поработать с настоящим знатоком своего дела!! Ты - умничка, Ирина))) Еще больших успехов тебе, новых творческих идей и вдохновения!!!!!\r\n\r\n', 1620733757),
(2, 5, 'Все прошло очень хорошо, мне понравилось. С Вами приятно иметь дело, впечатления положительные, получил в своей жизни определенный опыт.\r\nРезультатом остался доволен. Все сделано, все выполнено.\r\nТак что, если что, буду иметь Вас в виду.\r\nБольшое Вам спасибо.', 1620734757),
(3, 3, 'Все было замечательно! Все фотографии очень понравились, результатом очень довольна! Спасибо огромное за работу фотографа!', 1621335889),
(4, 4, 'Мне все очень понравилось, сложилось хорошее впечатление, прекрасная съёмка отснятых фотографий, получились стильные фотки. Думаю, ещё обращусь в будущем.', 1621335928),
(5, 6, 'Нам с мужем все очень понравилось! Очень хорошие впечатления остались от общения и процесса съемки! Большое спасибо за фотографии, остались очень довольны! Очень много красивых нежных фотографий и интересных ракурсов.\r\nИ погода позволила, дождем не облило)\r\nЖелаем дальнейших творческих успехов вашей команде! :)', 1621335999);

